import React, { Fragment, useContext, useRef } from "react";
// import "./style.scss";
import API from "../../utils/API";
import UserContext from "../../utils/UserContext";


function SignUpForm(props) {
  const { id, setId, name, setName, email, setEmail, loggedIn, setLoggedIn } =
    useContext(UserContext);
  const firstNameInput = useRef();
  const lastNameInput = useRef();
  const emailInput = useRef();
  const passwordInput = useRef();
  let extraProps = {}
  if (props.className) {
      extraProps.className = props.className;
  }
  let emailId = props.className ? props.className + "-signup-email" : "signup-email";
  let emailHelpId = props.className ? props.className + "-signup-email-help" : "signup-email-help";
  let passwordId = props.className ? props.className + "-signup-password" : "signup-password";
  const handleSubmit = (event) => {
    // if the user hits enter or hits the button, this function will fire
    event.preventDefault();
    console.log("submit happened");
    console.log({
      name: firstNameInput.current.value + " " + lastNameInput.current.value,
      email: emailInput.current.value,
      password: passwordInput.current.value,
    });
    // API.testUserRouter()
    // .then(data => {
    //     console.log(data);
    // })
    // .catch(err => {
    //     console.log(err);
    // });
    API.signup({
      first_name: firstNameInput.current.value,
      last_name: lastNameInput.current.value,
      email: emailInput.current.value,
      password: passwordInput.current.value,
    })
      .then((data) => {
        console.log(data);
        setId();
        setName();
        setEmail(data.data.email);
        setLoggedIn(true);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <Fragment>
      {(() => {
        if (!loggedIn) {
          return (
            <form {...extraProps} onSubmit={handleSubmit}>
              <div className="form-floating mb-3 input_field">
                <input
                  ref={firstNameInput}
                  type="text"
                  id="first_name"
                  className="inputBox firstInputBox"
                  placeholder="First Name"
                  name="first_name"
                  required=""
                />

                <input
                  ref={lastNameInput}
                  type="text"
                  id="last_name"
                  className="inputBox"
                  placeholder="Last Name"
                  name="last_name"
                  required=""
                />
 <label htmlFor={emailId}>Email address</label>
                <input
                  ref={emailInput}
                  type="email"
                  id="email"
                  className="inputBox"
                  placeholder="Email"
                  name="email"
                  required=""
                />

                <input
                  ref={passwordInput}
                  type="password"
                  id="password"
                  className="inputBox"
                  placeholder="Password"
                  name="password"
                  required=""
                />
              </div>
              <div className="landing_btn_container">
                <button
                  type="submit"
                  onClick={handleSubmit}
                  className="btn btn-outline-secondary signmeUpBtn"
                >
                  Sign Me Up
                </button>
              </div>

              <button to="/home">Cancel</button>
            </form>
            // <form {...extraProps} onSubmit={handleSubmit}>
            //     <div className="form-group">
            //         <label htmlFor={emailId}>Email address</label>
            //         <input ref={emailInput} type="email" className="form-control" id={emailId} aria-describedby={emailHelpId} />
            //         <small id={emailHelpId} className="email-help-text form-text text-muted">We'll never share your email with anyone else.</small>
            //     </div>
            //     <div className="form-group">
            //         <label htmlFor={passwordId}>Password</label>
            //         <input ref={passwordInput} type="password" className="form-control" id={passwordId} />
            //     </div>
            //     <button type="submit" className="btn btn-primary">Sign Up</button>
            // </form>
          );
        } else {
          return <h3>{email}</h3>;
        }
      })()}
    </Fragment>
  );
}

export default SignUpForm;
