import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import { ReusableBtn } from "../components/Buttons";
import {
  ListContainer,
  UnorderedList,
  ListElement,
  ImgTag,
} from "../components/ulElements";
import TemplateImage from "../images/sampleCatalog.png";
import Footer from "../components/Footer";
import API from "../utils/API";

function Catalogs() {
  const [outfits, setOutfits] = useState([]);
  const [catalogs, setCatalogs] = useState([]);
  const [catalogId, setCatalogId] = useState("");

  useEffect(() => {
    loadCatalogs();
  }, []);

  function loadCatalogs() {
    API.getCatalogs(1)
      .then((res) => {
        console.log([...res.data]);
        console.log([...res.data]);

        setCatalogs([...res.data]);

        // var catalogID = cata.filter(
        //     (catalog) => console.log(catalog.id));
        //   console.log(catalogID);
      })
      .catch((err) => console.log(err));
  }

  // function catalogID(){
  // var catalogID = catalog.filter(
  //   (catalog) => catalog.catalog === type
  // );
  // console.log(catalogID);
  // setCategoryId(catalogID);

  // }

  // useEffect(() => {
  //   loadOutfits();
  // }, []);

  // function loadOutfits() {
  //   API.getOutfits()
  //     .then((res) => {
  //       console.log(res);
  //               setOutfits([...res.data]);
  //     })
  //     .catch((err) => console.log(err));
  // }

  function handleOutfits(event) {
    console.log(event.target.innerHTML);

    // document.querySelector(".displayOutfits").style.display = "block";
    // var catalogID = catalogs.filter((catalog) => console.log(catalog.id));
    // console.log(catalogID);
  }

  return (
    <>
      <Header />
      <div className="topButtons">
        <ReusableBtn
          to="/item"
          className="newItemBtn btn btn-outline-secondary"
        >
          Add New Item
        </ReusableBtn>
        <ReusableBtn
          to="/buildoutfit"
          className="buildoutfitBtn btn btn-outline-secondary"
        >
          Build Outfit
        </ReusableBtn>
      </div>

      <ListContainer style={{ display: "flex" }} onClick={handleOutfits}>
        {catalogs.map((catalog) => (
          <UnorderedList
            key={catalog.id}
            id={catalog.id}
            className="ulElement"
            // onClick={handleOutfits}
          >
            <h3 key={catalog.id} id={catalog.id}>
              {catalog.catalog_name}
            </h3>
            <ListElement className="liElement">
              <ImgTag src={TemplateImage} className="imgElement" />
              <ImgTag src={TemplateImage} className="imgElement" />
              <ImgTag src={TemplateImage} className="imgElement" />
              <ImgTag src={TemplateImage} className="imgElement" />
            </ListElement>
          </UnorderedList>
        ))}
      </ListContainer>

      <ListContainer className="displayOutfits">
        {outfits.length ? (
          console.log("should render outfits")
        ) : (
          <h3>No outfits available</h3>
        )}
      </ListContainer>

      <Footer />
    </>
  );
}

export default Catalogs;
