import React, { Fragment, useContext, useRef } from "react";
import { Link } from "react-router-dom";
import API from "../../utils/API";
import UserContext from "../../utils/UserContext";
import { useHistory } from "react-router-dom";

function LoginForm(props) {
  const { email, setEmail, loggedIn, setLoggedIn } = useContext(UserContext);
  const history = useHistory();
  const emailInput = useRef();
  const passwordInput = useRef();
  let extraProps = {};
  if (props.className) {
    extraProps.className = props.className;
  }
  let emailId = props.className
    ? props.className + "-login-email"
    : "login-email";
  let emailHelpId = props.className
    ? props.className + "-login-email-help"
    : "login-email-help";
  let passwordId = props.className
    ? props.className + "-login-password"
    : "login-password";
  const handleSubmit = (event) => {
    // if the user hits enter or hits the button, this function will fire
    event.preventDefault();
    // console.log("submit happened");
    // console.log({ email: emailInput.current.value, password: passwordInput.current.value});
    API.login({
      email: emailInput.current.value,
      password: passwordInput.current.value,
    })
      .then((data) => {
        // console.log(data);
        // setEmail(data.data.email);
        setLoggedIn(true);
        history.push("/");
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <Fragment>
      {(() => {
        if (!loggedIn) {
          return (
            <form {...extraProps} onSubmit={handleSubmit}>
              <div className="form-floating mb-3 input_field">
                <input
                  ref={emailInput}
                  type="email"
                  id={emailId}
                  aria-describedby={emailHelpId}
                  className="inputBox"
                  placeholder="Email"
                  name="email"
                  required=""
                />
                <small
                  id={emailHelpId}
                  className="email-help-text form-text text-muted"
                >
                  We'll never share your email with anyone else.
                </small>

                <input
                  ref={passwordInput}
                  type="password"
                  id={passwordId}
                  className="inputBox"
                  placeholder="Password"
                  name="password"
                  required=""
                />
              </div>
              <div className="landing_btn_container">
                <button
                  type="submit"
                  onClick={handleSubmit}
                  className="btn btn-outline-secondary signmeUpBtn"
                >
                  Login
                </button>
              </div>

              <Link
              to="/"
                className="btn btn-outline-secondary signmeUpBtn"
                role="button"
              >
                Google
              </Link>
            </form>
          );
        } else {
          return <h3>{email}</h3>;
        }
      })()}
    </Fragment>
  );
}

export default LoginForm;
